mapper.select:

        	SELECT  CASE WHEN COUNT(USER_ID) = 0  THEN "N"
				    ELSE "Y"
				    END                         AS DUP_YN
			FROM    MEM_INFO
			WHERE   DOMAIN_ID = #{sDOMAIN_ID}
			AND     USER_ID   = #{sUSER_ID};

mapper.insert:

        	INSERT INTO MEM_INFO (
					  DOMAIN_ID
					, USER_ID
					, USER_NM
					, USER_PW
					, PSNL_NO
					, BRTH
					, LUNAR_DIV
					, JOIN_DT
					, RTRE_DT
					, CARR_YY
					, CARR_MM
			      	, ZIP_CD_JI
			      	, ZIP_CD_DO
			      	, ADDR_JI
			      	, ADDR_DO
					, OFFICE_NUM
					, PHONE_NUM
					, EMAIL
					, PHOTO
					, COOP_ID
					, BIZ_QUARTER
					, USER_PSTN
					, EMP_TYPE
					, PMS_AUTH
					, CURR_PRJT_ID )
			VALUES (
					  #{sDOMAIN_ID}
					, #{sUSER_ID}
					, #{sUSER_NM}
					, #{sUSER_PW}
					, #{sPSNL_NO}
					, #{sBRTH}
					, #{sLUNAR_DIV}
					, REPLACE(#{sJOIN_DT}, '.', '')
					, REPLACE(#{sRTRE_DT}, '.', '')
					, #{sCARR_YY}
					, #{sCARR_MM}
			      	, #{sZIP_CD_JI}
			      	, #{sZIP_CD_DO}
			      	, #{sADDR_JI}
			      	, #{sADDR_DO}
					, #{sOFFICE_NUM}
					, #{sPHONE_NUM}
					, #{sEMAIL}
					, #{sPHOTO}
					, #{sCOOP_ID}
					, #{sBIZ_QUARTER}
					, #{sUSER_PSTN}
					, #{sEMP_TYPE}
					, #{sPMS_AUTH}
					, (CASE WHEN #{sCURR_PRJT_ID} = '' THEN '00000000' ELSE #{sCURR_PRJT_ID} END) );

mapper.delete:

        	DELETE FROM MEM_INFO
			WHERE  DOMAIN_ID     = #{sDOMAIN_ID}
			AND    USER_ID       = #{sUSER_ID};

mapper.select:

SELECT  IFNULL(MAX(SEQ_NO) + 1, 1)  SEQ_NO
FROM    MEM_SCHL_INFO
WHERE   DOMAIN_ID = #{sDOMAIN_ID}
AND     USER_ID   = #{sUSER_ID};

mapper.insert:

INSERT INTO MEM_SCHL_INFO (
		  DOMAIN_ID
		, USER_ID
		, SEQ_NO
		, SCHL_NM
		, SCHL_DEPT
		, GRAD_YYYYMM )
	VALUES (
		  #{sDOMAIN_ID}
		, #{sUSER_ID}
		, #{sSEQ_NO}
		, #{sSCHL_NM}
		, #{sSCHL_DEPT}
		, #{sGRAD_YYYYMM} );

mapper.delete:

        	DELETE FROM MEM_SCHL_INFO
			WHERE  DOMAIN_ID     = #{sDOMAIN_ID}
			AND    USER_ID       = #{sUSER_ID};

