mapper.select:

SELECT  IFNULL(MAX(A.SEQ_NO) + 1, 1)  SEQ_NO
FROM    PMS_RLT_COL B
        LEFT OUTER JOIN MEM_PRJT_CARR_INFO A
        ON    A.DOMAIN_ID = #{sDOMAIN_ID}
        AND   A.USER_ID   = #{sUSER_ID}
WHERE   B.NUM      <= 1;

mapper.insert:

        	INSERT INTO MEM_PRJT_CARR_INFO (
					  DOMAIN_ID
				  	, USER_ID
				 	, SEQ_NO
					, PRJT_NM
					, STRT_YYYYMM
					, END_YYYYMM
					, ORDER_ORG_NM
					, EXEC_ORG_NM
					, PRJT_PSTN
					, ASSGN_BIZ
					, SERVER
					, OS
					, PRG_LANG
					, DBMS
					, ETC_INFO )
			VALUES (
					  #{sDOMAIN_ID}
					, #{sUSER_ID}
					, #{sSEQ_NO}
					, #{sPRJT_NM}
					, REPLACE(#{sSTRT_YYYYMM}, '.', '')
					, REPLACE(#{sEND_YYYYMM}, '.', '')
					, #{sORDER_ORG_NM}
					, #{sEXEC_ORG_NM}
					, #{sPRJT_PSTN}
					, #{sASSGN_BIZ}
					, #{sSERVER}
					, #{sOS}
					, #{sPRG_LANG}
					, #{sDBMS}
					, #{sETC_INFO} );

mapper.delete:

        	DELETE FROM MEM_PRJT_CARR_INFO
			WHERE  DOMAIN_ID = #{sDOMAIN_ID}
			AND    USER_ID   = #{sUSER_ID}
			AND    SEQ_NO    = #{sSEQ_NO};


        	DELETE FROM MEM_PRJT_CARR_INFO
			WHERE  DOMAIN_ID = #{sDOMAIN_ID}
			AND    USER_ID   = #{sUSER_ID};
